import React, { useRef, useState, useEffect }  from 'react';
import { default as FirstTest } from './test';
import './App.css';

function App() {
    const [needToResize, setNeedToResize] = useState(false);
    const menuContainerEl = useRef(null);
    useEffect(() => {
        if (needToResize && menuContainerEl && menuContainerEl.current) {
            let height = 0;
            Array.from(menuContainerEl.current.children).forEach(child => {
             height += child.getBoundingClientRect().height;
            });
            console.log(height);
            setNeedToResize(false);
        }
    }, [needToResize]);
  return (
    <div ref={menuContainerEl} className="App">
      <FirstTest animationId={1} setNeedToResize={setNeedToResize} />
      <FirstTest animationId={2} setNeedToResize={setNeedToResize} />
      <FirstTest animationId={3} setNeedToResize={setNeedToResize} />
    </div>
  );
}

export default App;
