import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';

import './menu-badge.scss';

const MenuBadge = ({
  children,
  type,
}) => {
  const className = classnames('menu-badge', {
    [`menu-badge--${type}`]: type !== '',
  });

  return (
    <span className={className}>
      {children}
    </span>
  );
};

MenuBadge.propTypes = {
  children: PropTypes.oneOfType([
    PropTypes.node,
    PropTypes.string,
    PropTypes.element,
  ]).isRequired,
  type: PropTypes.oneOf(['warning', 'error', 'default']),
};

MenuBadge.defaultProps = {
  type: '',
};

export default MenuBadge;
