import React, { useRef, useState, useEffect }  from 'react';
import { default as FirstTest } from './test';
import './App.css';

function App() {
    const [needToResize, setNeedToResize] = useState(false);
    const menuContainerEl = useRef(null);
    useEffect(() => {
        if (needToResize && menuContainerEl) {
            let height;
            console.log(menuContainerEl);
            menuContainerEl.current.child.forEach(child => {
             height += child.getBoundingClientRect().height;
            });
            setNeedToResize(false);
        }
    }, [needToResize]);
  return (
    <div ref={menuContainerEl} className="App">
      <FirstTest setNeedToResize={setNeedToResize} />
      <FirstTest setNeedToResize={setNeedToResize} />
      <FirstTest setNeedToResize={setNeedToResize} />
    </div>
  );
}

export default App;
