import React, { useRef, useState, useEffect }  from 'react';
import MenuSubItem from './MenuSubItem';
import './Menu.css';

const Menu = () => {
    const [needToResize, setNeedToResize] = useState(false);
    const menuContainerEl = useRef(null);
    return (
        <div className="menu-wrapper" ref={menuContainerEl}>
            <MenuSubItem animationId={1} items={[1,2,3]} setNeedToResize={setNeedToResize} />
        </div>
    );
}

export default Menu;
