import React, { useRef, useState, useEffect }  from 'react';
import MenuSubItem from './MenuSubItem';
import './Menu.css';

const Menu = () => {
    const [needToResize, setNeedToResize] = useState(false);
    const menuContainerEl = useRef(null);
    useEffect(() => {
        if (needToResize && menuContainerEl && menuContainerEl.current) {
            const translate = menuContainerEl.current.children[0].getBoundingClientRect().height * needToResize.scale.y;
            menuContainerEl.current.children[1].style.transform = `translateY(${translate}px)`;
            // menuContainerEl.current.style.transform = `translateY(${translate})`;
            console.log(translate);
            // menuContainerEl.current.style.transform = `scale(1, ${needToResize.scale.y})`;
            let height = 0;
            /* Array.from(menuContainerEl.current.children).forEach(child => {
             height += child.getBoundingClientRect().height;
            });*/
            setNeedToResize(false);
        }
    }, [needToResize]);
    return (
        <div className="menu-wrapper" ref={menuContainerEl}>
            <MenuSubItem animationId={1} items={[1,2,3]} setNeedToResize={setNeedToResize} />
        </div>
    );
}

export default Menu;
