import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { FormattedMessage } from 'react-intl';
/* eslint-disable no-unused-vars */
import {
  Icon,
  IconAnalyse,
  IconBox,
  IconMail,
  IconBoxes,
  IconFlash,
  IconContent,
  IconWheel,
} from '@manomano/toolkit';
import classnames from 'classnames';
import chevronDown from 'assets/svg/chevron-down.svg';

import MenuSub from '../MenuSub';

const icon = {
  analyse: IconAnalyse,
  box: IconBox,
  mail: IconMail,
  boxes: IconBoxes,
  flash: IconFlash,
  content: IconContent,
  wheel: IconWheel,
};

const MenuItem = ({ item, activeLocation }) => {
  const [isOpen, openMenu] = useState(false);

  const isActive = (to, basePath) => activeLocation === to || activeLocation.startsWith(basePath);

  const classNameLI = classnames('menu__nav-item', {
    'menu__nav-item--active': isActive(item.to, item.basePath),
    'menu__nav-item--sub': !item.to,
  });
  const classNameLink = classnames('menu__nav-link', {
    'menu__nav-link--active': isActive(item.to, item.basePath),
  });
  const classNameBtn = classnames('menu__nav-link', {
    'menu__nav-link--active': isOpen,
  });

  const menuItemIcon = item.icon ? icon[item.icon] : IconWheel;
  return (
    <li className={classNameLI} key={item.id}>
      <Icon className="menu__nav-img" component={menuItemIcon} fill="#FFFFFF" size={16} />
      {item.to && <Link
        to={{
          pathname: item.to,
          state: { title: item.title },
        }}
        className={classNameLink}
      >
        <FormattedMessage id={item.title} />
      </Link>}
      {!item.to &&
        <button className={classNameBtn} onClick={() => openMenu(!isOpen)}>
          <FormattedMessage id={item.title} />
          <span className="menu__chevron-container">
            <img src={chevronDown} alt="chevron" />
          </span>
        </button>
      }
      {item.children && item.children.length > 0 &&
      <MenuSub items={item.children} isOpen={isOpen} activeLocation={activeLocation} />}
    </li>
  );
};

MenuItem.propTypes = {
  item: PropTypes.shape({}).isRequired,
  activeLocation: PropTypes.string.isRequired,
};

export default MenuItem;
